define("theme_boost_union/sectionsummaryactivities",[],function(){function init(){document.querySelectorAll(".section-summary-activities").forEach((function(summary){var section=summary.closest(".section");if(!section)return;var counts={};section.querySelectorAll(".activity").forEach((function(activity){var modclass=Array.from(activity.classList).find((function(c){return c.startsWith("modtype_")}));if(!modclass)return;var modname=modclass.substring("modtype_".length);counts[modname]=(counts[modname]||0)+1}));var parts=[];for(var modname in counts)parts.push(counts[modname]+" "+modname);parts.length&&(summary.textContent=parts.join(", "))}))}return{init:init}});
